import { arrayUnion, doc, getDoc, updateDoc } from "firebase/firestore";
import { firestore } from "../firebase_setup/firebase";

const addToWishList = async (type, userId, showId) => {
    const currentUserRef = doc(firestore, 'users', userId);
    
    await updateDoc(currentUserRef, {
        watchlist: arrayUnion({showId, type})
    });
};

const getAllFromWatchlist = async (userId) => {
    const currentUserRef = doc(firestore, 'users', userId);
    const curremtUser = getDoc(currentUserRef)
    .then(result => console.log(result))
}

export {
    addToWishList,
    getAllFromWatchlist
}
