import { useState } from 'react';
import styles from './AddNew.module.css';

export const AddNew = () => {

    const [imageList, setImageList] = useState(['']);
    const [actorsList, setActorsList] = useState([{ fullName: '', imageUrl: '', wikiUrl: '' }])

    const handleInputChange = (e, index) => {
        const { name, value } = e.target;
        if (name === 'movieImgUrl') {
            const list = [...imageList];
            list[index] = value;
            setImageList(list);
        } else {
            const list = [...actorsList];
            list[index][name] = value;
            setActorsList(list);
        }
    };

    // handle click event of the Remove button
    const handleRemoveClick = (e, index, inputName) => {
        e.preventDefault();
        if (inputName === 'movie') {
            const list = [...imageList];
            list.splice(index, 1);
            setImageList(list);
        } else {
            const list = [...actorsList];
            list.splice(index, 1);
            setActorsList(list);
        }
    };

    // handle click event of the Add button
    const handleAddClick = (inputName) => {
        if (inputName === 'movie') {
            setImageList([...imageList, '']);
        } else {
            setActorsList([...actorsList, { fullName: '', imageUrl: '', wikiUrl: '' }])
        }
    };

    return (
        <section action="" className={styles['form']}>
            <h2 className={styles.heading}>Add new title</h2>
            <form action="" className={styles.form}>
                <label htmlFor="title">Title</label>
                <input type="text" name='title' id='title' className={styles.input} />
                <label htmlFor="year">Year</label>
                <input type="number" name='year' id='year' className={styles.input} />
                <label htmlFor="genre">Genre</label>
                <input type="text" name='genre' id='genre' className={styles.input} />
                <label htmlFor="desc">Description</label>
                <textarea name='desc' id='desc' className={styles.input} />
                <div className={styles.images}>
                    <label htmlFor="imgUrl">Image URL</label>
                    {/* <button onClick={addNewInput} className={`btn`}><i className="fa-solid fa-plus"></i></button> */}
                    {imageList.map((x, i) => {
                        return (
                            <div className="box" key={i}>
                                <input
                                    className={styles.input}
                                    name="movieImgUrl"
                                    value={x}
                                    onChange={e => handleInputChange(e, i)}
                                />

                                <div className="btn-box">
                                    {imageList.length !== 1 && <button
                                        className="btn"
                                        onClick={(e) => handleRemoveClick(e, i, 'movie')}><i className="fa-solid fa-minus"></i></button>}
                                    {imageList.length - 1 === i && <button className="btn" onClick={() => handleAddClick('movie')}><i className="fa-solid fa-plus"></i></button>}
                                </div>
                            </div>
                        );
                    })}
                    {imageList.join(', ')}

                    <label htmlFor="imgUrl">Actors</label>

                    {actorsList.map((x, i) => {
                        return (
                            <div className="box" key={i}>
                                <input
                                    className={styles.input}
                                    name="fullName"
                                    placeholder="Enter Full Name"
                                    value={x.fullName}
                                    onChange={e => handleInputChange(e, i)}
                                />

                                <input
                                    className={styles.input}
                                    name="imageUrl"
                                    placeholder="Enter Image URL"
                                    value={x.imageUrl}
                                    onChange={e => handleInputChange(e, i)}
                                />

                                <input
                                    className={styles.input}
                                    name="wikiUrl"
                                    placeholder="Enter Link to Wikipedia"
                                    value={x.wikiUrl}
                                    onChange={e => handleInputChange(e, i)}
                                />

                                <div className="btn-box">
                                    {imageList.length !== 1 && <button
                                        className="btn"
                                        onClick={(e) => handleRemoveClick(e, i, 'actor')}><i className="fa-solid fa-minus"></i></button>}
                                    {imageList.length - 1 === i && <button className="btn" onClick={() => handleAddClick('actor')}><i className="fa-solid fa-plus"></i></button>}
                                </div>
                            </div>
                        )
                    })}

                </div>
            </form>
        </section>
    )
};
