import styles from './ShowCard.module.css';

export const ShowCard = ({ show }) => {
    return (
        <article>
            <div className={styles['img-container']}>
                <img src={show.imageUrl} alt="show" />
            </div>
            <div className={styles.content}>
                <div className={styles.rating}>
            <i class="fa-solid fa-star"></i>
            <p className={styles['rating-points']}>{show.rating}</p>
            </div>
            <div className={styles.showInfo}>
                <p className={styles.title}>{show.title}</p>
                
                    {show.year.length === 4 ?
                    <p className={styles.year}>{show.year}</p>
                    : <p className={styles.year}>`${show.year.toString().slice(4)} ${show.year.toString().slice(4)}`</p>
                    }
                    
                    
            </div>
            </div>
        </article>
    );
};
