import { useEffect, useState } from "react";
import styles from './RatingModal.module.css'

export const RatingModal = ({ closeRateModal, rateShow, hasRatedWith, rateShowHandler, title }) => {
    const [rating, setRating] = useState(hasRatedWith);
    const [hover, setHover] = useState(0);

    const outsideClickHandler = (e) => {
        if (e.target.tagName === 'SECTION') {
            setTimeout(() => closeRateModal(), 300)
            // closeRateModal();
        }
    };

    useEffect(() => {
        setRating(hasRatedWith)
    }, [hasRatedWith]);

    return (
        <section onClick={outsideClickHandler} className={`${styles.modal} ${rateShow ? styles['active'] : styles['inactive']}`} >
            <div className={styles['modal-content']}>
                <span onClick={closeRateModal} className={styles['close']}>&times;</span>
                <h2 className={styles.title}>How would you rate {title} from 1 to 10?</h2>
                <div className={styles['star-rating']}>
                    {[...Array(10)].map((star, index) => {
                        index += 1;
                        return (

                            <button
                                type='button'
                                key={index}
                                // className={index <= (hover || rating) ? 'on' : 'off'}
                                onClick={() => setRating(index)}
                                onMouseEnter={() => setHover(index)}
                                onMouseLeave={() => setHover(rating)}
                                onDoubleClick={() => {
                                    setRating(0);
                                    setHover(0);
                                }}
                            >
                                <span className={styles.star}>
                                    {index <= (hover || rating) ?
                                        <i className="fa-solid fa-star"></i>
                                        : <i className="fa-regular fa-star"></i>
                                    }</span>
                            </button>
                        );
                    })}
                </div>
                    <button onClick={() => rateShowHandler(rating)} className={`${styles['rate-btn']} btn`}>Rate</button>
                <p>Some text in the Modal..</p>
            </div>

        </section>
    );
};
