import { Link } from 'react-router-dom';
import styles from './Hero.module.css';

export const Hero = () => {
    return (
        <div className={styles.hero}>
            <h1>Join the community of LMDb now!</h1>
            <h2>Here we share the passion for old and new movies and series!</h2>
            <button className='btn btn-primary'><Link className='link' to='/register'>Register</Link></button>
        </div>
    );
}
