import { useEffect, useState } from "react";
import { useParams } from "react-router-dom"
import { getOne } from "../../services/showsService";
import styles from './ShowDetails.module.css';

export const ShowDetails = () => {
    const { showId, type } = useParams();
    const [show, setShow] = useState({});

    useEffect(() => {
        getOne(showId, type)
            .then(result => setShow(result.fields));
    }, [showId, type]);

    return (
        <>
        {show.title &&<div className={styles['sections-wrapper']}>
        <section className={styles['details-section']}>
            <div className={styles['img-wrapper']}>
                <div className={styles['img-container']}>
                    <img src={show.imageList[0]} alt="" className={styles['selected']} />
                    <div className={styles['small-img-container']}>
                    {show.imageList.slice(1).map(x => <img src={x} alt="" className={styles['not-selected']} />)}
                    </div>
                </div>
            </div>
        </section>
        </div>}
        </>
    );
};
