import { createContext, useContext, useEffect, useState } from "react";
import { getAll, getAllFromWatchlist } from "../services/showsService";
import { useAuthContext } from "./AuthContext";

const ShowsContext = createContext();
export const useShowsContext = () => useContext(ShowsContext);



export const ShowsProvider = ({ children }) => {
    const [moviesList, setMoviesList] = useState([]);
    const [seriesList, setSeriesList] = useState([]);
    const [watchlist, setWatchlist] = useState([]);
    const { currentUser } = useAuthContext();

    useEffect(() => {

        getAll('Movie')
        .then(docs => {
                docs.forEach((doc) => {
                    // arr.push({
                    //     id: doc.id,
                    //     fields: doc.fields
                    // });
                    setMoviesList(state => [...state, {id: doc.id, fields: doc.fields}]);
                });
                // setMoviesList(arr);
        });
        getAll('Series')
        .then(docs => {
                docs.forEach((doc) => {
                    // arr.push({
                    //     id: doc.id,
                    //     fields: doc.fields
                    // });
                    setSeriesList(state => [...state, {id: doc.id, fields: doc.fields}]);
                });
                // setMoviesList(arr);
        });
        getAllFromWatchlist(currentUser.uid)
        .then(result => {
            console.log(result);
            setWatchlist(result);
        })
    }, [currentUser]);

    return <ShowsContext.Provider value={{moviesList, seriesList, watchlist}}>
        {children}
    </ShowsContext.Provider>
};
