import { useEffect, useState } from "react";
import { useAuthContext } from "../../contexts/AuthContext";
import { getAllFromWatchlist } from "../../services/userService";

export const Watchlist = () => {
    const { currentUser } = useAuthContext();
    const [watchlist, setWatchlist] = useState([]);
    useEffect(() => {
        setWatchlist(getAllFromWatchlist(currentUser.uid));
    }, [currentUser]);
    return (
        <div>
        {watchlist.length > 0 && watchlist.map(x => <p>{x.id}</p>)}
        </div>
    )

};
