import React, { useEffect, useState } from "react";
import { useAuthState } from "react-firebase-hooks/auth";
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";
import { useAuthContext } from "../../../contexts/AuthContext";
import { auth } from "../../../firebase_setup/firebase";
import { useInput } from "../../../hooks/useInput";
import { sendPasswordReset } from "../../../services/authService";
import userValidation from "../../../validationFunctions/userValidation";
import "./Reset.css";

export const Reset = () => {
    const email = useInput(userValidation.emailIsValid);

    const [ loading, error] = useAuthState(auth);
    const { currentUser } = useAuthContext();
    const [isLoading, setIsLoading] = useState(false);

    const navigate = useNavigate();

    const formIsValid = email.fieldIsValid;


    useEffect(() => {
        if (currentUser?.uid) {
            navigate("/");
        }
    }, [currentUser, loading]);

    const onPasswordResetHandler = () => {
        if(formIsValid) {
            sendPasswordReset(email.value);
        };
    };


    return (
