//             // closeRateModal();
//         }
//     };

//     return (
    <>
        <section onClick={outsideClickHandler} className={`${styles.modal} ${deleteModal ? styles['active'] : styles['inactive']}`} >
            <div className={styles['modal-content']}>
                <span onClick={closeDeleteModal} className={styles['close']}>&times;</span>
                <h2 className={styles.title}>Are you sure you want to delete your comment?</h2>

                <div className={styles['action-btns']}>
                    <button
                        onClick={() => closeDeleteModal()}
                        className={`btn`}
                    >Cancel
                    </button>
                    <button
                        onClick={() => deleteCommentHandler()}
                        className={`btn`}
                    >Delete
                    </button>

                </div>

            </div>

        </section>
//     );
// };

        <section className="form-section auth">
            <form className="form" onSubmit={onPasswordResetHandler}>
                <h2 className={'heading'}>Reset Password</h2>
                <div className={'input-box'}>
                    {/* <label className={'label'} htmlFor="email">Email</label> */}
                    {email.hasError && <p className={'alert'}>Email should be valid!</p>}
                    <input
                        type="text"
                        className={`input ${email.hasError && 'input-alert'}`}
                        value={email.value}
                        onChange={email.onChange}
                        onBlur={email.onBlur}
                        placeholder="E-mail Address"
                        id="email"
                    />
                </div>
                
                <button
                disabled={!formIsValid || isLoading}
                    className="btn action-btn"
                >
                    Send password reset email
                </button>
            </form>
            <div className="link anchor">
                Don't have an account? <Link className="link" to="/register">Register</Link> now.
            </div>
    </section>
    </>
    );
};
