import { useState } from "react";
import styles from './RatingModal.module.css'

export const RatingModal = ({ closeRateModal, rateShow }) => {
    const [rating, setRating] = useState(0);
    const [hover, setHover] = useState(0);

    const outsideClickHandler = (e) => {
        if (!e.target.hasClass('modal-content')) {
            closeRateModal();
        }
    };
    return (
        <div className={styles['modal-wrapper']}>
        <section onClick={outsideClickHandler} className={`${styles.modal} ${rateShow ? styles['active'] : ''}`} >
            <div className={styles['modal-content']}>
                <span onClick={closeRateModal} className={styles['close']}>&times;</span>
                <div className={styles['star-rating']}>
                    {[...Array(10)].map((star, index) => {
                        index += 1;
                        return (

                            <button
                                type='button'
                                key={index}
                                // className={index <= (hover || rating) ? 'on' : 'off'}
                                onClick={() => setRating(index)}
                                onMouseEnter={() => setHover(index)}
                                onMouseLeave={() => setHover(rating)}
                                onDoubleClick={() => {
                                    setRating(0);
                                    setHover(0);
                                }}
                            >
                                <span className={styles.star}>
                                    {index <= (hover || rating) ?
                                        <i className="fa-solid fa-star"></i>
                                        : <i className="fa-regular fa-star"></i>
                                    }</span>
                            </button>
                        );
                    })}
                </div>
                <p>Some text in the Modal..</p>
            </div>

        </section>
        </div>
    );
};
