import React, { useEffect, useState } from "react";
import { useAuthState } from "react-firebase-hooks/auth";
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";
import { useAuthContext } from "../../../contexts/AuthContext";
import { auth } from "../../../firebase_setup/firebase";
import { useInput } from "../../../hooks/useInput";
import { sendPasswordReset } from "../../../services/authService";
import userValidation from "../../../validationFunctions/userValidation";
import "./Reset.css";
function Reset() {
    const email = useInput(userValidation.emailIsValid);

    const [ loading, error] = useAuthState(auth);
    const { currentUser } = useAuthContext();
    const [isLoading, setIsLoading] = useState(false);

    const navigate = useNavigate();

    const formIsValid = email.fieldIsValid;


    useEffect(() => {
        if (currentUser) {
            navigate("/");
        }
    }, [currentUser, loading]);

    const onPasswordResetHandler = () => {
        if(formIsValid) {
            sendPasswordReset(email.value);
        };
    };


    return (

        <section className="form-section auth">
        <div className="login__container">
            <form className="form" onSubmit={onPasswordResetHandler}>
                <h2 className={'heading'}>Reset Password</h2>
                <div className={'input-box'}>
                    {/* <label className={'label'} htmlFor="email">Email</label> */}
                    {email.hasError && <p className={'alert'}>Email should be valid!</p>}
                    <input
                        type="text"
                        className={`input ${email.hasError && 'input-alert'}`}
                        value={email.value}
                        onChange={email.onChange}
                        onBlur={email.onBlur}
                        placeholder="E-mail Address"
                        id="email"
                    />
                </div>
                
                <button
                disabled={!formIsValid || isLoading}
                    className="btn action-btn"
                >
                    Send password reset email
                </button>
            </form>
            <div className="link anchor">
                Don't have an account? <Link className="link" to="/register">Register</Link> now.
            </div>
        </div>

    </section>
        // <div className="reset">
        //     <div className="reset__container">
        //         <input
        //             type="text"
        //             className="reset__textBox"
        //             value={email}
        //             onChange={(e) => setEmail(e.target.value)}
        //             placeholder="E-mail Address"
        //         />
        //         <button
        //             className="reset__btn"
        //             onClick={() => sendPasswordReset(email)}
        //         >
        //             Send password reset email
        //         </button>
        //         <div>
        //             Don't have an account? <Link to="/register">Register</Link> now.
        //         </div>
        //     </div>
        // </div>
    );
}
export default Reset;
