import React, { useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { auth } from "../../../firebase_setup/firebase";
import { useAuthState } from "react-firebase-hooks/auth";
import "./Login.css";
import { logIn, registerWithGoogle } from "../../../services/authService";
import { useAuthContext } from "../../../contexts/AuthContext";
import userValidation from "../../../validationFunctions/userValidation";
import { useInput } from "../../../hooks/useInput";
export const Login = () => {

    const [loading] = useAuthState(auth);
    const { currentUser } = useAuthContext();
    const navigate = useNavigate();


    const email = useInput(userValidation.isEmpty);
    const password = useInput(userValidation.isEmpty);


    useEffect(() => {
        if (loading) {
            // maybe trigger a loading screen
            return;
        }
        if (currentUser) {
            console.log('already logged in');
            navigate('/');
        };
    }, [currentUser]);

    const onLoginHandler = async (e) => {
        e.preventDefault();
        const result = await logIn(email, password);
        navigate('/');
        return result;
    };

    const onGoogleRegisterHandler = () => {
        registerWithGoogle();
    };

    return (
        <section className="login">
            <div className="login__container">
                <form className="login__container" onSubmit={onLoginHandler}>
                    <input
                        type="text"
                        className="login__textBox"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="E-mail Address"
                    />
                    <input
                        type="password"
                        className="login__textBox"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Password"
                    />
                    <button
                        className="login__btn"
                    >
                        Login
                    </button>
                </form>
                <button className="login__btn login__google" onClick={onGoogleRegisterHandler}>
                    Login with Google
                </button>
                <div className="login__link">
                    <Link to="/reset">Forgot Password</Link>
                </div>
                <div className="login__link">
                    Don't have an account? <Link to="/register">Register</Link> now.
                </div>
            </div>

        </section>
    //     <div className={styles['input-box']}>
    //     <label className={styles.label} htmlFor="duration">Duration</label>
    //     <input
    //         type="number"
    //         name='duration'
    //         id='duration'
    //         className={styles.input}
    //         onChange={handleSimpleInputChange}
    //     />
    // </div>

    )
}
