import { addDoc, collection } from "firebase/firestore";
import { firestore } from "../firebase_setup/firebase";

export const addNewMovie = async (showData) => {
    try{
    await addDoc(collection(firestore, "movies"), {
        showData,
        rating: ''
    });
} catch (err) {
    console.error(err);
    alert(err.message);
}
};
