//             const show = await getOne(x.showId, x.type);
//             setWatchlist(state => [...state, { id: show.id, fields: show.fields }]);
//             console.log(watchlist);
//         });

//     }, [userWatchlist]);

    return <CommentsContext.Provider value={{ comments }}>
        {children}
    </CommentsContext.Provider>
};
