import { useEffect, useState } from 'react';
import { useAuthContext } from '../../contexts/AuthContext';
import { useCommentsContext } from '../../contexts/CommentsContext';
import { getAllComments, postComment } from '../../services/commentsService';
import { CommentItem } from './CommentItem/CommentItem';
import styles from './CommentSection.module.css';

export const CommentSection = ({ showId }) => {

    const [openWriteReview, setOpenWriteReview] = useState(false);
    const [commentContent, setCommentContent] = useState('');
    const { currentUser } = useAuthContext();
    const [comments, setComments] = useState([]);
    
    useEffect(() => {
        getAllComments(showId)
        .then(result => {
            console.log(result);
            setComments(result)
        });

    }, [showId]);
    


    const changeHandler = (e) => {
        setCommentContent(e.target.value)
    };

    const postCommentHandler = async () => {
        const result = await postComment(showId, currentUser.uid, commentContent);
        console.log(result);
    }
    return (
        <section className={styles['comment-section']}>
            {comments.length > 0 ? comments.map(x => <CommentItem comment={x} key={x.id}  userId={currentUser?.uid}/>)
            : <p className={styles['no-results']}>Nothing to show</p>}
            {currentUser?.uid && !openWriteReview && <button
                onClick={() => setOpenWriteReview(true)}
                className={`btn ${'btn-write-review'}`}>
                Write your review
            </button>}
            {openWriteReview &&
                <div className={styles['write-review']}>
                    <textarea className={`box-shadow`} onChange={changeHandler} value={commentContent} name="comment" id="comment" cols="30" rows="10"></textarea>
                    <button
                        onClick={() => postCommentHandler()}
                        className={`btn`}>
                        Send
                    </button>
                </div>}
        </section>
    );
}
