    const [commentContent, setCommentContent] = useState('');

    const changeHandler = (e) => {
        setCommentContent(e.target.value)
    };

    const postComment = (commentContent) => {

    }
    return (
        <section>
            {!openWriteReview && <button
                onClick={() => setOpenWriteReview(true)}
                className={`btn`}>
                Write your review
            </button>}
            {openWriteReview &&
                <div className={styles['write-review']}>
                    <textarea onChange={changeHandler} value={commentContent} name="comment" id="comment" cols="30" rows="10"></textarea>
                    <button
                onClick={() => postComment(commentContent)}
                className={`btn`}>
                Send
            </button>
                </div>}
        </section>
    );
}
