import { addDoc, collection, getDocs, orderBy, query, serverTimestamp } from "firebase/firestore";
import { firestore } from "../firebase_setup/firebase";
import { getUserAdditionalData } from "./authService"

const commentCollection = collection(firestore, 'comments');

export const postComment = async (showId, ownerId, commentContent) => {
    
    const owner = await getUserAdditionalData(ownerId);
    try {
        return await addDoc(commentCollection, {
            showId,
            commentContent,
            likes: [],
            dislikes: [],
            ownerId,
            ownerName: owner.name,
            createdAt: serverTimestamp()
        });
    } catch (err) {
        console.error(err);
        alert(err.message);
    }
};

export const getAllComments = async () => {
    const orderedQuery = query(commentCollection, orderBy('createdAt', 'asc'));
    try {
        const result = await getDocs(orderedQuery)
            .then(docs => {
                let arr = [];

                docs.forEach((doc) => {
                    let fields = doc.data();

                    arr.push({
                        id: doc.id,
                        fields: fields
                    });
                });
                return arr;
            });
        // console.log(result);
        return result;

    } catch (err) {
        console.error(err);
        alert(err.message);
    }
}
