import { useState } from 'react';
import { timeAgoHandler } from '../../../utils/timeAgoHadler';
import styles from './CommentItem.module.css';

export const CommentItem = ({
    comment,
    userId
}) => {

    const [hasLiked, setHasLiked] = useState(comment.fields.likes.includes(userId));
    const [hasDisliked, setHasDisliked] = useState(comment.fields.dislikes.includes(userId));

    const sendReactionHandler = (reaction) => {
        reaction === 'like' ? setHasLiked(true) : setHasDisliked(true);
    };

    const removeReactionHandler = (reaction) => {
        reaction === 'like' ? setHasLiked(false) : setHasDisliked(false);
        
    };

    const timeAgo = timeAgoHandler(comment.fields.createdAt);
    return (
        <article className={styles['comment-wrapper']}>
            <header className={styles['header']}>
                <p className={styles['author']}>{comment.fields.ownerName}</p>
                <div className={styles['header-right']}>
                    <span>Likes: {comment.fields.likes.length}</span>
                    <span>Dislikes: {comment.fields.dislikes.length}</span>
                </div>

            </header>
            <div className={styles['content']}>
                <div className={styles.buttons}>
                    {userId === comment.fields.ownerId ?
                        <>
                            <button className={styles.edit}><i className="fa-solid fa-pen-to-square"></i></button>
                            <button className={styles.delete}><i className="fa-solid fa-trash"></i></button>
                        </>
                        : <>
                            {(!hasLiked && !hasDisliked) ?
                                <>
                                    <button onClick={() => sendReactionHandler('like')} className={styles.like}><i className={`fa-solid fa-thumbs-up ${styles['like']}`}></i></button>
                                    <button onClick={() => sendReactionHandler('dislike')} className={styles.like} className={styles.dislike}><i className={`fa-solid fa-thumbs-down ${styles['dislike']}`}></i></button>
                                </>
                                :  hasLiked ?
                                    <button onClick={() => removeReactionHandler('like')} className={styles.like} className={styles.like} > <i className={`fa-solid fa-thumbs-up ${styles['selected']} ${styles['like']}`}></i></button>
                                    : <button onClick={() => removeReactionHandler('dislike')} className={styles.dislike}><i className={`fa-solid fa-thumbs-down ${styles['selected']} ${styles['dislike']}`}></i></button>
                                
                            }
                        </>
                    }
                </div>
                <p className={styles.text}>{comment.fields.commentContent}</p>
                <span className={styles.date}>{timeAgo}</span>
            </div>
        </article >
    );
}
