export const durationParser = (time) => {
    const minute = 1;
    const hour = 60 * minute;

    if(time / hour >= 1) {
        return `${Math.floor(time/hour)}h ${time%hour}m`;
    } else {
        return `${time%hour}m`;
    }
};

export const textShortener = (text) => {
    if(text.length > 395) {
        return `${text.slice(0, 395)}...`
    } else {
        return text;
    }
}
