import { useShowsContext } from "../../../contexts/ShowsConext";
import { ShowCard } from "../../shared/ShowCard/ShowCard";

export const NewestMovies = () => {
    const { moviesList, watchlist, setWatchlist, styles } = useShowsContext();

    return (
        <section className={styles.section}>
            <div className={styles['title-wrapper']}>
                <div className={styles.devider}></div>
            <h2 className={styles.title}>Newest movies</h2>
            <i className="fa-solid fa-angle-right"></i>
            </div>
            <div className={styles.movies}>
                {moviesList.length > 0 ? moviesList.map(x =>
                    <ShowCard key={x.id} show={x.fields} id={x.id} watchlist={watchlist} setWatchlist={setWatchlist}/>)
                    : <p style={{ color: 'red', fontSize: '22px' }}>Nothing to show</p>}
            </div>
        </section>
    );
};
